import React, { useState, useEffect } from 'react';
import axios from 'axios';
import VehicleForm from './VehicleForm';
import MaintenanceForm from './MaintenanceForm';

function Dashboard({ onLogout }) {
  const [vehicles, setVehicles] = useState([]);
  const [maintenanceRecords, setMaintenanceRecords] = useState({});
  const [selectedVehicle, setSelectedVehicle] = useState(null);

  useEffect(() => {
    fetchVehicles();
  }, []);

  const fetchVehicles = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/vehicles', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setVehicles(response.data);
      response.data.forEach(vehicle => fetchMaintenanceRecords(vehicle.id));
    } catch (error) {
      console.error('Error fetching vehicles:', error);
    }
  };

  const fetchMaintenanceRecords = async (vehicleId) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/maintenance/${vehicleId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setMaintenanceRecords(prev => ({
        ...prev,
        [vehicleId]: response.data
      }));
    } catch (error) {
      console.error('Error fetching maintenance records:', error);
    }
  };

  return (
    <div>
      <div className="header">
        <h1>Vehicle Management Dashboard</h1>
        <button onClick={onLogout} className="btn">Logout</button>
      </div>

      <div className="card">
        <h2>Add New Vehicle</h2>
        <VehicleForm onVehicleAdded={fetchVehicles} />
      </div>

      <div className="card">
        <h2>Your Vehicles</h2>
        {vehicles.map(vehicle => (
          <div key={vehicle.id} className="card">
            <h3>{vehicle.model} - {vehicle.registration_number}</h3>
            <p>Type: {vehicle.vehicle_type}</p>
            <p>Purchase Date: {new Date(vehicle.purchase_date).toLocaleDateString()}</p>

            <h4>Maintenance Records</h4>
            {maintenanceRecords[vehicle.id]?.map(record => (
              <div key={record.id} className="card">
                <p>Type: {record.maintenance_type}</p>
                <p>Date: {new Date(record.maintenance_date).toLocaleDateString()}</p>
                <p>Cost: ${record.cost}</p>
              </div>
            ))}

            <MaintenanceForm 
              vehicleId={vehicle.id} 
              onMaintenanceAdded={() => fetchMaintenanceRecords(vehicle.id)} 
            />
          </div>
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
